import React, { Component } from "react";

class Form extends Component {

  render() {
    return (
      <form className="d-flex border rounded w-100 p-2 bg-danger">
        <input
          className="rounded pl-2 py-0"
          type="text"
          placeholder="Search Topic"
          name="searchTerm"
          value={this.props.value}
          onChange={this.props.handleInputChange}
        />

        <button className="btn btn-light" onClick={this.props.handleFormSubmit}>Submit</button>
      </form>
    );
  }
}

export default Form;
