import React, { Component } from "react";

class Form extends Component {
  // Setting the initial values of this.state.search and this.state.password
  state = {
    search: ""
  };

  // handle any changes to the input fields
  handleInputChange = event => {
    // Pull the name and value properties off of the event.target (the element which triggered the event)
    const { search, value } = event.target;

    // Set the state for the appropriate input field
    this.setState({
      [search]: value
    });
  };

  // When the form is submitted, prevent the default event and alert the search and password
  handleFormSubmit = event => {
    event.preventDefault();
    alert(`Search: ${this.state.search}`);
    this.setState({ search: "" });
  };

  render() {
    return (
      <form>
        <p>Search: {this.state.search}</p>
        <input
          type="text"
          placeholder="Search Topic"
          name="search"
          value={this.state.search}
          onChange={this.handleInputChange}
        />

        <button onClick={this.handleFormSubmit}>Submit</button>
      </form>
    );
  }
}

export default Form;
